let isMobile={Android:function(){return navigator.userAgent.match(/Android/i)},BlackBerry:function(){return navigator.userAgent.match(/BlackBerry/i)},iOS:function(){return navigator.userAgent.match(/iPhone|iPad|iPod/i)},Opera:function(){return navigator.userAgent.match(/Opera Mini/i)},Windows:function(){return navigator.userAgent.match(/IEMobile/i)},any:function(){return isMobile.Android()||isMobile.BlackBerry()||isMobile.iOS()||isMobile.Opera()||isMobile.Windows()}};document.addEventListener("click",e=>{if(!e.target.closest(".info-header__column"))for(let e of document.querySelectorAll(".info-header__column"))e.classList.remove("_show")});const infoHeader=document.querySelector(".info-header");infoHeader&&infoHeader.addEventListener("click",e=>{(e.target.classList.contains("info-header__current-lang")||e.target.classList.contains("_icon"))&&e.target.parentElement.classList.toggle("_show")});